mapper.insert:
insert into member (
	mid, mpassword, mname, mphone, memail, mrole, menable, mcreatedat
) values (
	#{mid}, #{mpassword}, #{mname}, #{mphone}, #{memail}, #{mrole},
	#{menable}, sysdate
)

mapper.select:
select mid, mpassword, mname, mphone, memail, mrole, menable, mcreatedat, mupdatedat
from member
where mid = #{mid}

select mid, memail
from member
where mphone = #{mphone}
