mapper.insert:
insert into member (
	mid, mpassword, mname, mphone, memail, mrole, menable, mcreatedat
) values (
	#{mid}, #{mpassword}, #{mname}, #{mphone}, #{memail}, #{mrole},
	#{menable}, sysdate
)

mapper.select:
select mid, memail
from member
where mphone = #{mphone}

mapper.update:
update member set
	mpassword = #{mpassword}, memail = #{memail}, mphone = #{mphone}
where mid = #{mid}

update member set
	menable = #{menable}
where mid = #{mid}
